					$("[qtip='"+this.id+"']").mouseleave(function(){
						hideConnections();
					});
					if( $(".qtip-content","[qtip='"+this.id+"']").height() > 200 ){
						$(".qtip-content","[qtip='"+this.id+"']").css('height','200px');
						$(".qtip-content","[qtip='"+this.id+"']").css('overflow','auto');
					}
